script:
/* Center stage */
.car-stage {
  position: relative;
  min-height: 320px;
  display: flex;
  justify-content: center;
  align-items: center;
}

/* All images stacked and hidden */
.car-img {
  position: absolute;
  max-width: min(560px, 90%);
  height: auto;
  opacity: 0;
  visibility: hidden;
  transform: translateY(6px) scale(.98);
  transition: opacity .35s ease, transform .35s ease, visibility 0s linear .35s;
}

/* Active image is visible */
.car-img.active {
  opacity: 1;
  visibility: visible;
  transform: translateY(0) scale(1);
  transition: opacity .35s ease, transform .35s ease, visibility 0s;
}

